-- Verificar total inserido
SELECT 
    COUNT(*) as total_propriedades,
    SUM(area_total) as area_total_ha,
    SUM(valor_atual) as valor_total_reais
FROM propriedades 
WHERE organizacao_id = 'yxhazxmvpazrcfwxzdaz';

-- Resumo por estado
SELECT 
    estado,
    COUNT(*) as quantidade_propriedades,
    ROUND(SUM(area_total)::numeric, 2) as area_total_ha,
    TO_CHAR(SUM(valor_atual), 'FM999,999,999,990.00') as valor_total_reais
FROM propriedades 
WHERE organizacao_id = 'yxhazxmvpazrcfwxzdaz'
GROUP BY estado
ORDER BY estado;